{"changed":true,"filter":false,"title":"users_controller.rb","tooltip":"/app/controllers/users_controller.rb","value":"class UsersController < ApplicationController\n  before_action :logged_in_user, only: [:index, :edit, :update]\n  before_action :correct_user,   only: [:edit, :update]\n  before_action :set_user, only: [:show, :edit, :update, :destroy]\n  before_action :admin_user,     only: :destroy\n  \n  # GET /users\n  # GET /users.json\n  def index\n    @users = User.paginate(page: params[:page])\n  end\n\n  # GET /users/1\n  # GET /users/1.json\n  def show\n    @user = User.find(params[:id])\n  end\n\n  # GET /users/new\n  def new\n    @user = User.new\n  end\n\n  # GET /users/1/edit\n  def edit\n    @user = User.find(params[:id])\n  end\n\n  # POST /users\n  # POST /users.json\n  def create\n      @user = User.new(user_params)\n    if @user.save\n      log_in @user\n      flash[:success] = \"Welcome to the ALU Learning Management Dashboard!\"\n      redirect_to @user # Handle a successful save.\n    else\n      render 'new'\n    end\n  end\n\n  # PATCH/PUT /users/1\n  # PATCH/PUT /users/1.json\n  def update\n    @user = User.find(params[:id])\n    if @user.update_attributes(user_params)\n      flash[:success] = \"Profile updated\"\n      redirect_to @user\n    else\n      render 'edit'\n    end\n  end\n\n  # DELETE /users/1\n  # DELETE /users/1.json\n  def destroy\n    @user.destroy\n    respond_to do |format|\n      format.html { redirect_to users_url, notice: 'User was successfully destroyed.' }\n      format.json { head :no_content }\n    end\n  end\n\n  private\n    # Use callbacks to share common setup or constraints between actions.\n    def set_user\n      @user = User.find(params[:id])\n    end\n\n    # Never trust parameters from the scary internet, only allow the white list through.\n    def user_params\n      params.require(:user).permit(:name, :email, :password,\n                                   :password_confirmation)\n    end\n    \n    # Before filters\n\n    # Confirms a logged-in user.\n    def logged_in_user\n      unless logged_in?\n        store_location\n        flash[:danger] = \"Please log in.\"\n        redirect_to login_url\n      end\n    \n        # Confirms the correct user.\n    def correct_user\n      @user = User.find(params[:id])\n      redirect_to(root_url) unless current_user?(@user)\n    end\n        # Confirms an admin user.\n    def admin_user\n      redirect_to(root_url) unless current_user.admin?\n    end\n    \n    end\nend\n","undoManager":{"mark":69,"position":100,"stack":[[{"group":"doc","deltas":[{"start":{"row":29,"column":23},"end":{"row":29,"column":24},"action":"insert","lines":[" "]}]}],[{"group":"doc","deltas":[{"start":{"row":29,"column":6},"end":{"row":30,"column":0},"action":"insert","lines":["",""]},{"start":{"row":30,"column":0},"end":{"row":30,"column":6},"action":"insert","lines":["      "]}]}],[{"group":"doc","deltas":[{"start":{"row":29,"column":6},"end":{"row":29,"column":52},"action":"insert","lines":["flash[:success] = \"Welcome to the Sample App!\""]}]}],[{"group":"doc","deltas":[{"start":{"row":29,"column":49},"end":{"row":29,"column":50},"action":"remove","lines":["p"]}]}],[{"group":"doc","deltas":[{"start":{"row":29,"column":48},"end":{"row":29,"column":49},"action":"remove","lines":["p"]}]}],[{"group":"doc","deltas":[{"start":{"row":29,"column":47},"end":{"row":29,"column":48},"action":"remove","lines":["A"]}]}],[{"group":"doc","deltas":[{"start":{"row":29,"column":46},"end":{"row":29,"column":47},"action":"remove","lines":[" "]}]}],[{"group":"doc","deltas":[{"start":{"row":29,"column":45},"end":{"row":29,"column":46},"action":"remove","lines":["e"]}]}],[{"group":"doc","deltas":[{"start":{"row":29,"column":44},"end":{"row":29,"column":45},"action":"remove","lines":["l"]}]}],[{"group":"doc","deltas":[{"start":{"row":29,"column":43},"end":{"row":29,"column":44},"action":"remove","lines":["p"]}]}],[{"group":"doc","deltas":[{"start":{"row":29,"column":42},